module.exports = validateInput

function validateInput ({ type, e }) {
  var value
  if (type === 'int') {
    if (value != parseInt(value, 10)) { alert('Value has to be an integer') }
  }
  else if (type === 'uint') {
    value = e.target.value
    if (value < 0) { alert('Value can not be less than 0') }
    if (value != parseInt(value, 10)) { alert('Value has to be an integer') }
  }
}
